mapper.insert:
INSERT INTO BOOK(
    BOOK_NO
    , MEMBER_NO
    , BOOK_PLACE
    , START_DATE
    , END_DATE
    , BOOK_PURPOSE
    )
VALUES(
    SEQ_BOOK_NO.NEXTVAL
    , #{memberNo}
    , #{bookPlace}
    , #{startDate}
    , #{endDate}
    , #{bookPurpose}
	)

mapper.update:
UPDATE BOOK SET
BOOK_PLACE = #{bookPlace},
START_DATE = #{startDate, jdbcType=TIMESTAMP},
END_DATE = #{endDate, jdbcType=TIMESTAMP},
BOOK_PURPOSE = #{bookPurpose}
, UPDATE_DATE = SYSDATE WHERE BOOK_NO = #{bookNo}
