mapper.insert:
INSERT INTO MEMBER
(
    NO
    ,NAME
    ,ID
    ,PWD
    ,EMAIL
    ,TEL
    ,ADDRESS
    ,DEPART_NO
    ,COMPANY_NO
    ,POSITION_NO
)
VALUES
(
    SEQ_MEMBER_NO.NEXTVAL
    ,{name}
    ,{id}
    ,{pwd}
    ,{email}
    ,{tel}
    ,{address}
    ,{departNo}
    ,{companyNo}
    ,{positionNo}
)

mapper.update:
UPDATE MEMBER
SET
ANNUAL = {annual}
WHERE NO = {no}

UPDATE MEMBER
SET
WORK_STATUS_NO = {workStatusNo}
WHERE NO = {no}

UPDATE MEMBER
SET
AUTH_NO = {authNo}
WHERE NO = {no}

mapper.insert:
INSERT INTO COMPANY
(
    NO
    , NAME
    , MANAGER_NO
)
VALUES
(
    SEQ_COMPANY_NO.NEXTVAL
    , {companyName}
    , {no}
)
