mapper.insert:
		INSERT INTO MESSAGE 
		(
			NO
			,SEND_NO
			,RECIEVE_NO
			,CONTENT
			,SEND_DATE
			,DEL_YN
		) 
   	VALUES 
  		(
   		#{no}
   		, #{sendNo}
   		, #{recieveNo}
   		, #{content}
   		, SYSDATE
   		, 'N'
  		)

mapper.update:
 		UPDATE MESSAGE 
		    SET DEL_YN = 'Y'
		    , DELETE_DATE = SYSDATE 
	    WHERE NO = #{no} 
	    AND RECVE_NO = #{recieveNo};
