mapper.insert:
INSERT INTO NOTICE (
    NOTICE_NO
    , MEMBER_NO
    , TITLE
    , CONTENT
    , CATEGORY
    , OPEN_DEPART
    )
VALUES (
    SEQ_NOTICE_NO.NEXTVAL
    , #{memberNo}
    , #{title}
    , #{content}
    , #{category}
    , #{openDepart}
)

mapper.update:
	UPDATE NOTICE
	SET


 		TITLE = #{title}


	    , CONTENT = #{content}


        , FILE_PATH = #{filePath}


        , CATEGORY = #{category}


        , UPDATE_DATE = SYSDATE


	WHERE NOTICE_NO = #{noticeNo}
	AND DELETE_YN = 'N'

		UPDATE NOTICE
    	SET DELETE_YN = 'Y'
		WHERE NOTICE_NO = #{noticeNo}
