mapper.insert:
	 	INSERT INTO VACATION_FORM 
	 	(
	 		NO
	 		,VACATION_NO
	 		,WRITER_NO
	 		,FIRST_APPLY_NO
	 		,MID_APPLY_NO
	 		,LAST_APPLY_NO
	 		,CATEGORY_NO
	 		,START_TIME
	 		,FINISH_TIME
	 		,CONTENT
	 		,WRITE_DATE
	 	)
		VALUES 
		(
		    SEQ_VACATION_FORM_NO.NEXTVAL
		    ,#{vacationNo}
		    ,#{writerNo}
		    ,#{firstApplyNo}
		    ,#{midApplyNo}
		    ,#{lastApplyNo}
		    ,#{categoryNo}
			,TO_DATE(#{startTime}, 'YYYY-MM-DD')
        	,TO_DATE(#{finishTime}, 'YYYY-MM-DD')
		    ,#{content}
		    ,SYSDATE
		)

mapper.update:
	UPDATE VACATION_FORM 
   SET CATEGORY_NO = 
                     (
                     SELECT NO 
                     FROM DOCUMENT_CATEGORY 
                     WHERE NO = 3
                     ) 
   , DOCUMENT_DATE = SYSDATE
   , REJECTION = #{rejection} 
   WHERE NO = #{no}

UPDATE VACATION_FORM 
	SET DEL_YN = 'Y' 
WHERE NO = #{no}
